html {
  height: 100%;
  font-size: 10px;
}

body {
  height: 100%;
  max-height: 100vh;
  min-height: 100vh;
  font-family: "Montserrat", sans-serif;
  font-weight: normal;
}

button {
  background-color: transparent;
  border: none;
}
button:is(button:focus, button:active) {
  outline: none;
}

.card-page {
  display: flex;
  align-items: center;
  justify-content: center;
  height: 100%;
}

.buttons-box {
  display: flex;
  flex-direction: column;
  width: clamp(300px, 50vw, 50rem);
  margin: 0 auto;
  margin-top: 10rem;
}
.buttons-box button {
  margin-bottom: 1rem;
  padding: 2rem;
  font-size: 18px;
  background-color: #007bff;
  border-radius: 6px;
  color: #fff;
}
.buttons-box button:hover {
  background-color: #1a88ff;
}

.welcome-content {
  text-align: center;
  padding-top: 3rem;
  width: 500px;
  margin: 0 auto;
}
.welcome-content p {
  font-size: 1.6rem;
}

.modal-overlay {
  position: fixed;
  top: 0;
  left: 0;
  right: 0;
  bottom: 0;
  height: 100%;
  width: 100%;
  background-color: rgba(0, 0, 0, 0);
  z-index: 100;
  transition: all 0.3s ease;
  display: none;
}
.modal-overlay.open {
  display: block;
}
.modal-overlay .modal-content {
  position: absolute;
  padding: 1.5rem 0;
  box-shadow: 0px 4px 8px 1px rgba(0, 0, 0, 0.11);
  border-radius: 10px;
  background-color: #fff;
}
.modal-overlay .modal-content__item {
  display: flex;
  align-items: center;
  padding: 0 1.5rem;
  transition: all 0.3s;
}
.modal-overlay .modal-content__item:not(:last-child) {
  margin-bottom: 0.8rem;
}
.modal-overlay .modal-content__item .icon {
  width: 2.4rem;
  height: 2.4rem;
  margin-right: 1.5rem;
}
.modal-overlay .modal-content__item .text {
  font-weight: 500;
  font-size: 14px;
  line-height: 17px;
  color: rgba(0, 0, 0, 0.6);
}
.modal-overlay .modal-content__item:hover {
  background-color: #eeeaea;
  transition: all 0.3s;
}
.modal-overlay .profile-modal {
  top: 10%;
  left: 50%;
  width: 43.1rem;
  height: auto;
  min-height: 20rem;
  padding-top: 0;
  padding-bottom: 2rem;
}
.modal-overlay .profile-modal__header .top {
  position: relative;
  background: #FFFFFF;
  box-shadow: 0px 1px 1px rgba(0, 0, 0, 0.15);
  border-radius: 10px 10px 0 0;
  width: 100%;
  height: 3.3rem;
}
.modal-overlay .profile-modal__header .top .close {
  position: absolute;
  top: 7px;
  right: 7px;
  display: block;
  width: 2rem;
  height: 2rem;
  max-width: 2rem;
  background-position: 0 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url("/99365b4e87aeaf95545906a5e5edfaaa.svg");
}
.modal-overlay .profile-modal__header .bottom {
  padding-top: 2.5rem;
  padding-bottom: 2.7rem;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  background-color: #E9E8E8;
}
.modal-overlay .profile-modal__content {
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}
.modal-overlay .profile-modal__form {
  width: 95%;
  padding: 2.5rem 1rem;
  margin: 0 auto;
}
.modal-overlay .profile-modal .modal-form__inputs {
  width: 100%;
  display: flex;
  align-items: flex-start;
  margin-bottom: 2rem;
}
.modal-overlay .profile-modal .modal-form__inputs .left-block {
  flex-grow: 0;
}
.modal-overlay .profile-modal .modal-form__inputs .left-block__icon {
  margin-right: 2.8rem;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block {
  flex-grow: 1;
  display: flex;
  flex-direction: column;
  align-items: flex-start;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block .input-group {
  width: 100%;
  display: flex;
  align-items: flex-start;
  flex-direction: column;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block .input-group:not(:last-child) {
  margin-bottom: 1.5rem;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block .input-group input {
  width: 100%;
  border: none;
  font-weight: 500;
  font-size: 14px;
  line-height: 17px;
  letter-spacing: 0.15em;
  color: #000000;
  margin-bottom: 4px;
  padding: 0;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block .input-group input:focus {
  outline: none;
}
.modal-overlay .profile-modal .modal-form__inputs .right-block .input-group label {
  font-weight: 500;
  font-size: 10px;
  line-height: 12px;
  letter-spacing: 0.15em;
  color: rgba(0, 0, 0, 0.6);
}
.modal-overlay .profile-modal .modal-form__button {
  margin: 0 auto;
  margin-top: 4rem;
}
.modal-overlay .profile-modal-button {
  display: block;
  font-weight: 500;
  font-size: 10px;
  line-height: 12px;
  text-align: center;
  letter-spacing: 0.15em;
  color: #FFFFFF;
  background: rgba(100, 81, 136, 0.7);
  box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.25);
  border-radius: 6px;
  padding: 1rem;
  width: 15.7rem;
}

#chat {
  display: flex;
  height: 100%;
  background: radial-gradient(77.22% 100.7% at 22.78% 49.96%, rgba(255, 240, 105, 0.43) 1.01%, rgba(212, 242, 155, 0.72) 20.31%, rgba(130, 245, 252, 0.68) 100%);
  overflow: hidden;
}
#chat #sidebar {
  width: 20%;
  flex-grow: 0;
}
#chat #main {
  flex-grow: 1;
}

#sidebar {
  position: relative;
  background: #FAF9F9;
  box-shadow: 0px 0px 1px rgba(0, 0, 0, 0.1);
}

.sidebar {
  display: flex;
  flex-direction: column;
}
.sidebar__header {
  padding: 1rem 0.8rem 1rem 1.6rem;
  border: 1px solid rgba(44, 44, 44, 0.05);
  flex-grow: 0;
}
.sidebar__input-group {
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: space-between;
}
.sidebar__input-group .profile-open {
  display: block;
  width: 2.5rem;
  height: 2.5rem;
  max-width: 2.5rem;
  background-position: 0 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url("/d2f4590c7d4b783924be8fa81604d178.svg");
  margin-right: 6px;
  flex-grow: 0;
}
.sidebar__input-group .search-input {
  flex-grow: 1;
  font-weight: 300;
  font-size: 12px;
  line-height: 15px;
  color: black;
  border: none;
  background: rgba(44, 44, 44, 0.11);
  border-radius: 3px;
  padding: 0.6rem 1.5rem;
}
.sidebar__input-group .search-input::placeholder {
  color: rgba(0, 0, 0, 0.5);
}
.sidebar__input-group .search-input:focus {
  outline: none;
}
.sidebar__content {
  padding: 1rem 1.6rem 2rem 1rem;
  flex-grow: 1;
}
.sidebar .sidebar-content__item {
  display: flex;
  align-items: center;
  justify-content: flex-start;
  margin-bottom: 1rem;
}
.sidebar .sidebar-content__avatar {
  width: 4rem;
  height: 4rem;
  min-width: 4rem;
  border-radius: 50%;
  background: #C4C4C4;
  margin-right: 1.2em;
}
.sidebar .sidebar-content__avatar img {
  width: 4rem;
  height: 4rem;
  min-width: 4rem;
  object-position: center top;
  object-fit: cover;
  border-radius: 50%;
}
.sidebar .sidebar-content__preview {
  display: flex;
  flex-direction: column;
  overflow: hidden;
}
.sidebar .sidebar-content__preview .chat-name {
  font-size: 14px;
  line-height: 17px;
  color: #2c2c2c;
  font-weight: bold;
  margin-bottom: 6px;
}
.sidebar .sidebar-content__preview .text-preview {
  font-size: 10px;
  line-height: 12px;
  color: rgba(0, 0, 0, 0.5);
  display: flex;
  align-items: center;
}
.sidebar .sidebar-content__preview .text-preview .username {
  white-space: nowrap;
  margin-right: 5px;
  font-weight: 500;
  color: #CAAE19;
}
.sidebar .sidebar-content__preview .text-preview .text {
  margin: 0;
  white-space: nowrap;
}

#main {
  display: flex;
  flex-direction: column;
}
#main #header {
  flex-grow: 0;
}
#main #main-content {
  flex-grow: 1;
}
#main #footer {
  flex-grow: 0;
}

#header {
  position: relative;
  padding: clamp(1.2rem, 1.5vw, 1.6rem) clamp(1.5rem, 2vw, 2.2rem);
  background-color: #fff;
  background: rgba(44, 44, 44, 0.8);
  box-shadow: 0px 0px 2px 1px rgba(0, 0, 0, 0.1);
}
#header.chat-header {
  display: flex;
  align-items: center;
  justify-content: space-between;
}
#header .chat-header__title-block {
  display: flex;
  align-items: center;
  justify-content: flex-start;
}
#header .chat-header__title-block .chat-title {
  font-weight: 500;
  font-size: 12px;
  line-height: 15px;
  color: rgba(255, 255, 255, 0.75);
  margin-right: clamp(1rem, 1.8vw, 1.5rem);
}
#header .chat-header__title-block .chat-status {
  font-weight: bold;
  font-size: 12px;
  line-height: 15px;
  color: #CAAE19;
}
#header .chat-header__settings {
  display: block;
  width: 2.4rem;
  height: 2.4rem;
  max-width: 2.4rem;
  background-position: 0 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url("/32b8790f6b23abee2dfc5997bc575d39.svg");
}

.footer {
  padding: 9px 15px 9px 12px;
  background-color: #fff;
}
.footer__input-group {
  position: relative;
  display: flex;
  align-items: center;
  justify-content: space-between;
  width: 100%;
}
.footer__input-group .clip-icon {
  flex-grow: 0;
  margin-right: 12px;
  display: block;
  width: 2rem;
  height: 2rem;
  max-width: 2rem;
  background-position: 0 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url("/2f6de86b469f87964d14dbcce4e8c78c.svg");
}
.footer__input-group .send-input {
  flex-grow: 1;
  background: #FFFFFF;
  border-radius: 6px;
  color: #000;
  padding: 8px 26px 8px 10px;
  border: 1px solid rgba(0, 0, 0, 0.13);
  font-size: 1.6rem;
}
.footer__input-group .send-input::placeholder {
  color: rgba(0, 0, 0, 0.4);
}
.footer__input-group .send-input:focus {
  outline: none;
}
.footer__input-group .send__icon {
  display: block;
  position: absolute;
  top: 50%;
  right: 8px;
  transform: translate(0, -50%);
  display: block;
  width: 2.2rem;
  height: 2.2rem;
  max-width: 2.2rem;
  background-position: 0 0;
  background-size: contain;
  background-repeat: no-repeat;
  background-image: url("/5d111882e5044cdd9812939dba5e41b0.svg");
}

#sign-in .card {
  width: clamp(40rem, 50vw, 49.5rem);
  height: auto;
  box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.15);
  margin-right: 2rem;
  margin-left: 2rem;
  padding: 3rem clamp(1.5rem, 2vw, 2.5rem);
}
#sign-in .card__content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
  width: 90%;
  margin: 0 auto;
}
#sign-in .card__logo {
  width: 7.4rem;
  height: 7.4rem;
  min-width: 7.4rem;
  border-radius: 50%;
  background-color: #C4C4C4;
  margin-top: 4.7rem;
  margin-bottom: 3.5rem;
}
#sign-in .card__logo img {
  width: 7.4rem;
  height: 7.4rem;
  min-width: 7.4rem;
  object-position: center top;
  object-fit: cover;
  border-radius: 50%;
}
#sign-in .card__title {
  font-size: 24px;
  line-height: 29px;
  color: #000;
  margin-bottom: 1.6rem;
  font-weight: bold;
}
#sign-in .card__form {
  margin-bottom: clamp(1.5rem, 2.5vw, 2.5rem);
}
#sign-in .card__link {
  font-weight: 500;
  font-size: 16px;
  line-height: 20px;
  color: #3988FF;
  background-color: transparent;
  outline: none;
  border: none;
}
#sign-in .card__link:active {
  outline: none;
}
#sign-in .card__link:not(:last-child) {
  margin-bottom: 10px;
}
#sign-in .form {
  width: 100%;
}
#sign-in .form__input-group {
  width: 100%;
  margin-bottom: clamp(1rem, 1.2vw, 1.8rem);
}
#sign-in .form__submit-btn {
  font-weight: 500;
  font-size: 1.8rem;
  text-align: center;
  letter-spacing: 0.15em;
  display: block;
  width: 100%;
  border-radius: 6px;
  outline: none;
  border: none;
  font-style: normal;
  font-weight: normal;
  font-size: 1.6rem;
  line-height: 1.9rem;
  text-align: center;
  color: #FFFFFF;
  padding: 1.8rem 0;
  margin: 0 auto;
  transition: all 0.3s;
  background-color: #007BFF;
  border: 1px solid #007BFF;
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.25);
  margin-top: clamp(2rem, 2.5vw, 3rem);
}
#sign-in .form__submit-btn:disabled {
  opacity: 0.5;
}
#sign-in .form__submit-btn.ripple {
  background-position: center;
  transition: all 0.8s;
}
#sign-in .form__submit-btn.ripple:active {
  background-size: 100%;
  transition: all 0.3s;
}
#sign-in .form__submit-btn.ripple:hover {
  background: #007BFF radial-gradient(circle, transparent 5%, #007BFF 5%) center/15000%;
}
#sign-in .form__submit-btn.ripple:active {
  background-color: #1a88ff;
  background-size: 100%;
  transition: background 0s;
}
#sign-in .form .input-group {
  display: flex;
  flex-direction: column;
}
#sign-in .form .input-group__label {
  font-weight: 500;
  font-size: 1.2rem;
  line-height: 1.5rem;
  margin-bottom: 8px;
}
#sign-in .form .input-group__input {
  width: 100%;
  background: #FFFFFF;
  border: 1px solid #007BFF;
  border-radius: 6px;
  padding: clamp(1rem, 1.5vw, 1.5rem) clamp(1.2rem, 1.5vw, 1.7rem);
}
#sign-in .form .input-group__input:is(#sign-in .form .input-group__input:active, #sign-in .form .input-group__input:focus) {
  outline: none;
}
#sign-in .form .input-group__input::placeholder {
  font-weight: 500;
  font-size: 14px;
  line-height: 17;
}
#sign-in .form .input-group .error-message {
  margin: 0 auto;
  margin-top: 0.5rem;
  color: #DC3545;
  font-weight: 500;
  display: none;
}
#sign-in .form .input-group.success .input-group__input {
  border-color: #28A745;
}
#sign-in .form .input-group.error .input-group__input {
  border-color: #DC3545;
}
#sign-in .form .input-group.error .error-message {
  display: block;
}

#sign-up {
  padding-top: 10rem;
  padding-bottom: 10rem;
  height: unset;
}
#sign-up .card {
  width: clamp(40rem, 50vw, 49.5rem);
  height: auto;
  box-shadow: 0px 0px 4px rgba(0, 0, 0, 0.15);
  margin-right: 2rem;
  margin-left: 2rem;
  padding: 3rem clamp(1.5rem, 2vw, 2.5rem);
}
#sign-up .card__content {
  display: flex;
  align-items: center;
  justify-content: space-between;
  flex-direction: column;
  width: 90%;
  margin: 0 auto;
}
#sign-up .card__logo {
  width: 7.4rem;
  height: 7.4rem;
  min-width: 7.4rem;
  border-radius: 50%;
  background-color: #C4C4C4;
  margin-top: 4.7rem;
  margin-bottom: 3.5rem;
}
#sign-up .card__logo img {
  width: 7.4rem;
  height: 7.4rem;
  min-width: 7.4rem;
  object-position: center top;
  object-fit: cover;
  border-radius: 50%;
}
#sign-up .card__title {
  font-size: 24px;
  line-height: 29px;
  color: #000;
  margin-bottom: 1.6rem;
  font-weight: bold;
}
#sign-up .card__form {
  margin-bottom: clamp(1.5rem, 2.5vw, 2.5rem);
}
#sign-up .card__link {
  font-weight: 500;
  font-size: 16px;
  line-height: 20px;
  color: #3988FF;
  background-color: transparent;
  outline: none;
  border: none;
}
#sign-up .card__link:active {
  outline: none;
}
#sign-up .card__link:not(:last-child) {
  margin-bottom: 10px;
}
#sign-up .form {
  width: 100%;
}
#sign-up .form__input-group {
  width: 100%;
  margin-bottom: clamp(1rem, 1.2vw, 1.8rem);
}
#sign-up .form__submit-btn {
  font-weight: 500;
  font-size: 1.8rem;
  text-align: center;
  letter-spacing: 0.15em;
  display: block;
  width: 100%;
  border-radius: 6px;
  outline: none;
  border: none;
  font-style: normal;
  font-weight: normal;
  font-size: 1.6rem;
  line-height: 1.9rem;
  text-align: center;
  color: #FFFFFF;
  padding: 1.8rem 0;
  margin: 0 auto;
  transition: all 0.3s;
  background-color: #007BFF;
  border: 1px solid #007BFF;
  box-shadow: 0px 3px 4px rgba(0, 0, 0, 0.25);
  margin-top: clamp(2rem, 2.5vw, 3rem);
}
#sign-up .form__submit-btn:disabled {
  opacity: 0.5;
}
#sign-up .form__submit-btn.ripple {
  background-position: center;
  transition: all 0.8s;
}
#sign-up .form__submit-btn.ripple:active {
  background-size: 100%;
  transition: all 0.3s;
}
#sign-up .form__submit-btn.ripple:hover {
  background: #007BFF radial-gradient(circle, transparent 5%, #007BFF 5%) center/15000%;
}
#sign-up .form__submit-btn.ripple:active {
  background-color: #1a88ff;
  background-size: 100%;
  transition: background 0s;
}
#sign-up .form .input-group {
  display: flex;
  flex-direction: column;
}
#sign-up .form .input-group__label {
  font-weight: 500;
  font-size: 1.2rem;
  line-height: 1.5rem;
  margin-bottom: 8px;
}
#sign-up .form .input-group__input {
  width: 100%;
  background: #FFFFFF;
  border: 1px solid #007BFF;
  border-radius: 6px;
  padding: clamp(1rem, 1.5vw, 1.5rem) clamp(1.2rem, 1.5vw, 1.7rem);
}
#sign-up .form .input-group__input:is(#sign-up .form .input-group__input:active, #sign-up .form .input-group__input:focus) {
  outline: none;
}
#sign-up .form .input-group__input::placeholder {
  font-weight: 500;
  font-size: 14px;
  line-height: 17;
}
#sign-up .form .input-group .error-message {
  margin: 0 auto;
  margin-top: 0.5rem;
  color: #DC3545;
  font-weight: 500;
  display: none;
}
#sign-up .form .input-group.success .input-group__input {
  border-color: #28A745;
}
#sign-up .form .input-group.error .input-group__input {
  border-color: #DC3545;
}
#sign-up .form .input-group.error .error-message {
  display: block;
}

